import styled from "styled-components";

export default  function Filters({isCollapsed, setIsCollapsed}){

    return(<>

           { isCollapsed ?  (<div className="flex-20">
           <select>
               <option value="brave">Brave</option>
               <option value="safari">Safari</option>
               <option value="firefox">Firefox</option>
               <option value="edge">Edge</option>
               <option value="chrome">Chrome</option>
           </select>
       </div>) : 
            ( <div className="flex-10 flex justify-center align-start">
                <div className="filters" onClick={()=>setIsCollapsed(true)}>
            <Img src="./assets/images/Filter.png"/>
            <p style={{textAlign:"center", color:"#2C8EBF", padding:".5rem 1rem"}}>Filter</p>
            </div>
            </div>)
        }

    </>);
}


const Img = styled.img`
  width: 2rem;
  display:inline-block;
  margin-left:1.2rem;
  margin-top:.75rem;
`;
