import React from "react";
import styled from "styled-components";
import { Icon } from "./Icon";

export default function CollapsibleFilter({ handleToggleFilter, title, count , isClosed}) {
  return (
    <FilterBox className="flex justify-between">
      <FilterName onClick={handleToggleFilter}>{title}<span style={{color:"rgb(20,113,165)"}} isClosed={isClosed}>{count}</span></FilterName>
      <Icon
        path="./assets/images/downarrow.png"
        alt="down arrow"
        handleClick={handleToggleFilter}
      />
    </FilterBox>
  );
}

const FilterBox = styled.div`
  padding: 20px 5px 0 5px;
  border-top: 1px solid #e3e4e8;
  margin-bottom: 1rem;
`;

const FilterName = styled.p`
  color: ${({isClosed})=>{}} 
  font-size: 18px;
  font-weight: 500;
  cursor: pointer;
`;
