import React, { useState } from "react";
import styled from "styled-components";
import BrowserFilter from "../BrowserFilter/BorwserFilter";
import CountryFilter from "../CountryFilter";
import DateFilter from "../DateFilter";

export default function Filters({ isCollapsed, setIsCollapsed }) {
  const [filters, setFilters] = useState({
    browsers: [],
    country: [],
    date: [],
  });

  const handleChange = () => {};

  return (
    <>
      {isCollapsed ? (
        <div className="flex-20 filters">
          <Box className="flex justify-start align-center">
            <i class="fas fa-search"></i>
            <span>Search...</span>
          </Box>
          <BrowserFilter
            filters={filters}
            setFilters={setFilters}
            handleChange={handleChange}
          />
          <CountryFilter
            filters={filters}
            setFilters={setFilters}
            handleChange={handleChange}
          />
          <DateFilter
            filters={filters}
            setFilters={setFilters}
            handleChange={handleChange}
          />

<div className="collapse-filter" onClick={() => setIsCollapsed(true)}>
          <Collapse src="./assets/images/collapseicon.png"/>
      </div>
        </div>
      ) : (
        <div className="flex-10 flex justify-center align-start">
          <div className="filters" onClick={() => setIsCollapsed(true)}>
            <Img src="./assets/images/Filter.png" />
            <p
              style={{
                textAlign: "center",
                color: "#2C8EBF",
                padding: ".5rem 1rem",
              }}
            >
              Filter
            </p>
          </div>
        </div>
      )}
    </>
  );
}

const Box = styled.div`
  background-color: rgb(255, 255, 255);
  border: 1px solid #e3e4e8;
  border-radius: 5px;
  height: 45px;
  padding: 0 1rem;
  color: rgb(125, 125, 125);
  margin-bottom: 1rem;
  & span {
    display: inline-block;
    margin-left: 0.5rem;
  }
`;

const Img = styled.img`
  width: 2rem;
  display: inline-block;
  margin-left: 1.2rem;
  margin-top: 0.75rem;
`;

const Collapse = styled(Img)`
 width:.85rem;
 margin-left: .5rem;
`;
