import styled from "styled-components";
import BrowserFilter from "../BrowserFilter/BorwserFilter";
import CountryFilter from "../CountryFilter";
import DateFilter from "../DateFilter";

export default  function Filters({isCollapsed, setIsCollapsed}){

    return(<>

           { isCollapsed ?  (<div className="flex-20 filters">

             <Box className="flex justify-start align-center">
             <i class="fas fa-search"></i>
             <span>Search...</span>
             </Box>
            <BrowserFilter/>
            <CountryFilter/>
            <DateFilter/>
       </div>) : 
            ( <div className="flex-10 flex justify-center align-start">
                <div className="filters" onClick={()=>setIsCollapsed(true)}>
            <Img src="./assets/images/Filter.png"/>
            <p style={{textAlign:"center", color:"#2C8EBF", padding:".5rem 1rem"}}>Filter</p>
            </div>
            </div>)
        }

    </>);
}


const Box = styled.div`
background-color: rgb(255,255,255);
border: 1px solid #E3E4E8;
border-radius:5px;
height: 45px;
padding:0 1rem;
color:rgb(125,125,125);
margin-bottom:1rem
&  span{
    color:#6B727D;
    font-weight:500;
    mar
}
`;


const Img = styled.img`
  width: 2rem;
  display:inline-block;
  margin-left:1.2rem;
  margin-top:.75rem;
`;
