import React, { useState } from "react";
import CollapsibleFilter from "../Common/CollapsibleFilter";
import Select from "../Common/Select";

export default function CountryFilter({ filters, setFilters, handleChange }) {
  const [isFilterOpen, setIsFilterOpen] = useState(false);
  const countries = ["India", "China", "USA", "Brazil", "Uzbekistan", "Peru"];
  const { country: checkedOption } = filters;

  console.log(isFilterOpen, "isFilter");

  const handleOnChange = e => {
    const { id, checked } = e.target;

    const obj = {
      val: id,
      checked,
      key: "country",
    };

    handleChange(obj);
  };

  const handleToggleFilter = () => {
    setIsFilterOpen(!isFilterOpen);
  };

  return (
    <div>
      <CollapsibleFilter handleToggleFilter={handleToggleFilter} />

      {isFilterOpen && (
        <Select
          options={countries}
          checkedOption={checkedOption}
          handleOnChange={handleOnChange}
        />
      )}
    </div>
  );
}
