import React, { useState } from "react";
import styled from "styled-components";

import HeadBar from "../HeadBar";
import Table from "../Table";
import Filters from "../Filters";
import BreakdownDropdown from "../BreakdownDropdown";

export default function Main() {
  const [breakdown, setBreakdown] = useState(["Date"]);
  const [isCollapsed, setIsCollapsed] = useState(false);
  const [filters, setFilters] = useState({
    browsers: ["Chrome"],
    country: ["USA"],
    date: ["01"],
  });

  return (
    <>
      <MainBox className="container">
        <Heading>Leads unique by Email Address</Heading>

        <BreakdownDropdown
          setBreakdown={setBreakdown}
          breakdownOptions={breakdown}
        />

        <DataDisplay>
          <HeadBar />
          <div className="flex justify-between align-start">
            <Filters
              isCollapsed={isCollapsed}
              setIsCollapsed={setIsCollapsed}
              filters={filters}
              setFilters={setFilters}
            />
            <Table isCollapsed={isCollapsed} cols={breakdown} filters={filters}
              setFilters={setFilters} />
          </div>
        </DataDisplay>
      </MainBox>
    </>
  );
}

const MainBox = styled.main`
  padding: 2.5rem 3.5rem;
  background-color: rgb(248, 249, 250);
  border: 2px solid rgb(50, 50, 50);
  border-radius: 1rem;
`;

const Heading = styled.h1`
  font-size: 1.5rem;
  font-weight: 500;
  color: rgb(56, 67, 84);
  padding: 1rem 0;
`;

const DataDisplay = styled.div`
  background-color: rgb(255, 255, 255);
  border: 1px solid #e3e4e8;
  box-shadow: 0px 3px 3px rgba(177, 177, 177, 0.25);
  border-radius: 0.5rem;
`;

